import flet as ft
from .classes.abAppbar import abAppbar

def about_page(page, drawer):
    page.close(drawer)
    page.overlay.clear()
    page.controls.clear()
    page.add(abAppbar(page, drawer))
    page.add(ft.Column(
        controls=[
            ft.Row(
                controls=[ft.Image(height=140, width=140, src_base64="")],
                alignment=ft.MainAxisAlignment.CENTER
            ),
            ft.Row(
                controls=[ft.Text("LRCMake v0.1", font_family="Google Sans", size=32, weight=ft.FontWeight.W_200)],
                alignment=ft.MainAxisAlignment.CENTER
            ),
            ft.Row(
                controls=[ft.Text("With ♥️ by Dzheremi")], 
                alignment=ft.MainAxisAlignment.CENTER
            ),
            ft.Divider(thickness=2),
            ft.Row(
                controls=[ft.TextButton(text="Open App's GitHub", url="https://github.com/Dzheremi2/LRCMake")],
                alignment=ft.MainAxisAlignment.CENTER
            ),
            ft.Divider(thickness=2),
            ft.Column(controls=[
                ft.Row(controls=[ft.Text("3rd party resources", font_family="Google Sans", size=16, color="#A9A9A9")], alignment=ft.MainAxisAlignment.CENTER),
                ft.Row(controls=[ft.TextButton(text="LRClib", url="https://lrclib.net/")], alignment=ft.MainAxisAlignment.CENTER)]
            )
        ]
    ))
    page.update()